            .Offset(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;).FormulaR1C1 = &lt;span class=&quot;hljs-string&quot;&gt;&quot;=ROUND(RC[-1]*3600*24,2)&quot;&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;End&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;With&lt;/span&gt;

