    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; formData = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FormData&lt;/span&gt;();
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; photo = inp.&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;];      

    formData.&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;photo&quot;&lt;/span&gt;, photo);
    formData.&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;user&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(user));  

    &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; ctrl = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;AbortController&lt;/span&gt;() &lt;span class=&quot;hljs-comment&quot;&gt;// timeout&lt;/span&gt;
    &lt;span class=&quot;hljs-built_in&quot;&gt;setTimeout&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;() =&amp;gt;&lt;/span&gt; ctrl.&lt;span class=&quot;hljs-title function_&quot;&gt;abort&lt;/span&gt;(), &lt;span class=&quot;hljs-number&quot;&gt;50000&lt;/span&gt;);

    &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
       &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; r = &lt;span class=&quot;hljs-keyword&quot;&gt;await&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;fetch&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;/upload/image&apos;&lt;/span&gt;, 
         {&lt;span class=&quot;hljs-attr&quot;&gt;method&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;POST&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;body&lt;/span&gt;: formData, &lt;span class=&quot;hljs-attr&quot;&gt;signal&lt;/span&gt;: ctrl.&lt;span class=&quot;hljs-property&quot;&gt;signal&lt;/span&gt;}); 
       &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;HTTP response code:&apos;&lt;/span&gt;,r.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt;); 
       &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;success&apos;&lt;/span&gt;);
    } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt;(e) {
       &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Huston we have problem...:&apos;&lt;/span&gt;, e);
    }

