&lt;p&gt;In case you want to do it like that:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$.&lt;span class=&quot;hljs-title function_&quot;&gt;upload&lt;/span&gt;( form.&lt;span class=&quot;hljs-property&quot;&gt;action&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FormData&lt;/span&gt;( myForm))
.&lt;span class=&quot;hljs-title function_&quot;&gt;progress&lt;/span&gt;( &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt; progressEvent, upload&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;( progressEvent.&lt;span class=&quot;hljs-property&quot;&gt;lengthComputable&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; percent = &lt;span class=&quot;hljs-title class_&quot;&gt;Math&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;round&lt;/span&gt;( progressEvent.&lt;span class=&quot;hljs-property&quot;&gt;loaded&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;100&lt;/span&gt; / progressEvent.&lt;span class=&quot;hljs-property&quot;&gt;total&lt;/span&gt;) + &lt;span class=&quot;hljs-string&quot;&gt;&apos;%&apos;&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;( upload) {
            &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;( percent + &lt;span class=&quot;hljs-string&quot;&gt;&apos; uploaded&apos;&lt;/span&gt;);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;( percent + &lt;span class=&quot;hljs-string&quot;&gt;&apos; downloaded&apos;&lt;/span&gt;);
        }
    }
})
.&lt;span class=&quot;hljs-title function_&quot;&gt;done&lt;/span&gt;( &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&apos;Finished upload&apos;&lt;/span&gt;);                    
