&lt;p&gt;If you want to upload file using AJAX here is code which you can use for file uploading.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$(&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; options = { 
                &lt;span class=&quot;hljs-attr&quot;&gt;beforeSubmit&lt;/span&gt;:  showRequest,
        &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;:       showResponse,
        &lt;span class=&quot;hljs-attr&quot;&gt;dataType&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;json&apos;&lt;/span&gt; 
        }; 
    $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;body&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;delegate&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#image&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;change&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
        $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#upload&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;ajaxForm&lt;/span&gt;(options).&lt;span class=&quot;hljs-title function_&quot;&gt;submit&lt;/span&gt;();        
    }); 
});     
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;showRequest&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;formData, jqForm, options&lt;/span&gt;) { 
    $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#validation-errors&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;hide&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;empty&lt;/span&gt;();
    $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#output&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;css&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;display&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;none&apos;&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;; 
} 
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;showResponse&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;response, statusText, xhr, $form&lt;/span&gt;)  { 
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(response.&lt;span class=&quot;hljs-property&quot;&gt;success&lt;/span&gt; == &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; arr = response.&lt;span class=&quot;hljs-property&quot;&gt;errors&lt;/span&gt;;
        $.&lt;span class=&quot;hljs-title function_&quot;&gt;each&lt;/span&gt;(arr, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;index, value&lt;/span&gt;)
        {
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (value.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; != &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;)
            {
                $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#validation-errors&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;div class=&quot;alert alert-error&quot;&amp;gt;&amp;lt;strong&amp;gt;&apos;&lt;/span&gt;+ value +&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;/strong&amp;gt;&amp;lt;div&amp;gt;&apos;&lt;/span&gt;);
            }
        });
        $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#validation-errors&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;show&lt;/span&gt;();
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
         $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#output&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;html&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;img src=&apos;&quot;&lt;/span&gt;+response.&lt;span class=&quot;hljs-property&quot;&gt;file&lt;/span&gt;+&lt;span class=&quot;hljs-string&quot;&gt;&quot;&apos; /&amp;gt;&quot;&lt;/span&gt;);
         $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#output&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;css&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;display&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;block&apos;&lt;/span&gt;);
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Here is the HTML for Upload the file&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;form &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;form-horizontal&quot;&lt;/span&gt; id=&lt;span class=&quot;hljs-string&quot;&gt;&quot;upload&quot;&lt;/span&gt; enctype=&lt;span class=&quot;hljs-string&quot;&gt;&quot;multipart/form-data&quot;&lt;/span&gt; method=&lt;span class=&quot;hljs-string&quot;&gt;&quot;post&quot;&lt;/span&gt; action=&lt;span class=&quot;hljs-string&quot;&gt;&quot;upload/image&apos;&quot;&lt;/span&gt; autocomplete=&lt;span class=&quot;hljs-string&quot;&gt;&quot;off&quot;&lt;/span&gt;&amp;gt;
    &lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;input&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;type&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;image&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;image&quot;&lt;/span&gt; /&amp;gt;&lt;/span&gt;&lt;/span&gt; 
&amp;lt;/form&amp;gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; dataform = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FormData&lt;/span&gt;($(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#myform&quot;&lt;/span&gt;)[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]);
&lt;span class=&quot;hljs-comment&quot;&gt;//console.log(dataform);&lt;/span&gt;
$.&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;({
    &lt;span class=&quot;hljs-attr&quot;&gt;url&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;url&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;type&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;data&lt;/span&gt;: dataform,
    &lt;span class=&quot;hljs-attr&quot;&gt;async&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;res&lt;/span&gt;) {
        response data;
    },
    &lt;span class=&quot;hljs-attr&quot;&gt;cache&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;contentType&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;processData&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Here was an idea I was thinking of:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title class_&quot;&gt;Have&lt;/span&gt; an iframe on page and have a referencer.
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Have a form in which you move the input type file element to.&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title class_&quot;&gt;Form&lt;/span&gt;:  A processing page &lt;span class=&quot;hljs-variable constant_&quot;&gt;AND&lt;/span&gt; a target &lt;span class=&quot;hljs-keyword&quot;&gt;of&lt;/span&gt; the &lt;span class=&quot;hljs-variable constant_&quot;&gt;FRAME&lt;/span&gt;.
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;The result will post to the iframe, and then you can just send the fetched data up a level to the image tag you want with something like:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-attr&quot;&gt;data&lt;/span&gt;:image/png;base64,asdfasdfasdfasdfa
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;and the page loads.&lt;/p&gt;
&lt;p&gt;I believe it works for me, and depending you might be able to do something like:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;aftersubmit&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
    &lt;span class=&quot;hljs-title function_&quot;&gt;stopPropagation&lt;/span&gt;(); &lt;span class=&quot;hljs-comment&quot;&gt;// or some other code which would prevent a refresh.&lt;/span&gt;
});
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;input &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;form-control cu-b-border&quot;&lt;/span&gt; type=&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt; id=&lt;span class=&quot;hljs-string&quot;&gt;&quot;formFile&quot;&lt;/span&gt;&amp;gt;
&lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;img&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;myImg&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;src&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;#&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;In js&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;script&amp;gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; formData = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FormData&lt;/span&gt;();
    formData.&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;file&apos;&lt;/span&gt;, $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#formFile&apos;&lt;/span&gt;)[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]);
    $.&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;({
        &lt;span class=&quot;hljs-attr&quot;&gt;type&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;POST&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-attr&quot;&gt;url&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;/GetData/UploadImage&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-attr&quot;&gt;data&lt;/span&gt;: formData,
        &lt;span class=&quot;hljs-attr&quot;&gt;processData&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;, &lt;span class=&quot;hljs-comment&quot;&gt;// tell jQuery not to process the data&lt;/span&gt;
        &lt;span class=&quot;hljs-attr&quot;&gt;contentType&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;, &lt;span class=&quot;hljs-comment&quot;&gt;// tell jQuery not to set contentType&lt;/span&gt;
        &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;data&lt;/span&gt;) {
            &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(data);
            $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#myImg&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;src&apos;&lt;/span&gt;, data);
        },
        &lt;span class=&quot;hljs-attr&quot;&gt;error&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;xhr, ajaxOptions, thrownError&lt;/span&gt;) {
        }
    })
&amp;lt;/script&amp;gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;In controller&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;public &lt;span class=&quot;hljs-title class_&quot;&gt;ActionResult&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;UploadImage&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;HttpPostedFileBase&lt;/span&gt; file)
        {
            string filePath = &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;;
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (file != &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;)
            {
                string path = &lt;span class=&quot;hljs-string&quot;&gt;&quot;/uploads/Temp/&quot;&lt;/span&gt;;
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!&lt;span class=&quot;hljs-title class_&quot;&gt;Directory&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;Exists&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Server&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;MapPath&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;~&quot;&lt;/span&gt; + path)))
                {
                    &lt;span class=&quot;hljs-title class_&quot;&gt;Directory&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;CreateDirectory&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Server&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;MapPath&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;~&quot;&lt;/span&gt; + path));
                }
                filePath = &lt;span class=&quot;hljs-title class_&quot;&gt;FileUpload&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;SaveUploadedFile&lt;/span&gt;(file, path);
            }

            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Json&lt;/span&gt;(filePath, &lt;span class=&quot;hljs-title class_&quot;&gt;JsonRequestBehavior&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;AllowGet&lt;/span&gt;);
        }
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;/p&gt;&lt;div class=&quot;snippet&quot; data-lang=&quot;js&quot; data-hide=&quot;false&quot; data-console=&quot;true&quot; data-babel=&quot;false&quot;&gt;
&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#form-id&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;submit&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;) {
   e.&lt;span class=&quot;hljs-title function_&quot;&gt;preventDefault&lt;/span&gt;();
});

$(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#form-id&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;submit&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;) {

     &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; formObj = $(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;);
     &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; formURL = formObj.&lt;span class=&quot;hljs-title function_&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;action&quot;&lt;/span&gt;);
     &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; formData = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FormData&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;);
          $.&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;({
             &lt;span class=&quot;hljs-attr&quot;&gt;url&lt;/span&gt;: formURL,
             &lt;span class=&quot;hljs-attr&quot;&gt;type&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;data&lt;/span&gt;: formData,
             &lt;span class=&quot;hljs-attr&quot;&gt;processData&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;contentType&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;async&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;cache&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;enctype&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;multipart/form-data&quot;&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;dataType&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;json&quot;&lt;/span&gt;,
             &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;data&lt;/span&gt;) {
                 &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (data.&lt;span class=&quot;hljs-property&quot;&gt;success&lt;/span&gt;) {
                        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(data.&lt;span class=&quot;hljs-property&quot;&gt;success&lt;/span&gt;)
                 } 

                 &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;  (data.&lt;span class=&quot;hljs-property&quot;&gt;error&lt;/span&gt;) {
                        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(data.&lt;span class=&quot;hljs-property&quot;&gt;error&lt;/span&gt;)
                 } 
             }
         });
});&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;snippet-code-html lang-html s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;src&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;https://cdnjs.cloudflare.com/ajax/libs/jquery/1.7.1/jquery.min.js&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;

&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;form&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;class&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;form-horizontal&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;form-id&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;action&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;masterFileController&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;enctype&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;multipart/form-data&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;
