&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; ranar = [],
     red1 = &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;a&lt;/span&gt; =&amp;gt;&lt;/span&gt; reduced = [...a.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;p,c&lt;/span&gt;) =&amp;gt;&lt;/span&gt; p.&lt;span class=&quot;hljs-title function_&quot;&gt;set&lt;/span&gt;(c,&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;),&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Map&lt;/span&gt;()).&lt;span class=&quot;hljs-title function_&quot;&gt;keys&lt;/span&gt;()],
     red2 = &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;a&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;from&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Set&lt;/span&gt;(a)),
     avg1 = [],
     avg2 = [],
       ts = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
       te = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
     res1 = [],
     res2 = [],
     count= &lt;span class=&quot;hljs-number&quot;&gt;100&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i&amp;lt;count; i++){
  ranar = (&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;100000&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;fill&lt;/span&gt;(&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)).&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Math&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;floor&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Math&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;random&lt;/span&gt;()*&lt;span class=&quot;hljs-number&quot;&gt;10000000&lt;/span&gt;));
  ts = performance.&lt;span class=&quot;hljs-title function_&quot;&gt;now&lt;/span&gt;();
  res1 = &lt;span class=&quot;hljs-title function_&quot;&gt;red1&lt;/span&gt;(ranar);
  te = performance.&lt;span class=&quot;hljs-title function_&quot;&gt;now&lt;/span&gt;();
  avg1.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(te-ts);
  ts = performance.&lt;span class=&quot;hljs-title function_&quot;&gt;now&lt;/span&gt;();
  res2 = &lt;span class=&quot;hljs-title function_&quot;&gt;red2&lt;/span&gt;(ranar);
  te = performance.&lt;span class=&quot;hljs-title function_&quot;&gt;now&lt;/span&gt;();
  avg2.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(te-ts);
}

avg1 = avg1.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;p,c&lt;/span&gt;) =&amp;gt;&lt;/span&gt; p+c)/count;
avg2 = avg2.&lt;span class=&quot;hljs-title function_&quot;&gt;reduce&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;p,c&lt;/span&gt;) =&amp;gt;&lt;/span&gt; p+c)/count;

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;map &amp;amp; spread took: &quot;&lt;/span&gt; + avg1 + &lt;span class=&quot;hljs-string&quot;&gt;&quot;msec&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;set &amp;amp; A.from took: &quot;&lt;/span&gt; + avg2 + &lt;span class=&quot;hljs-string&quot;&gt;&quot;msec&quot;&lt;/span&gt;);&lt;/code&gt;&lt;/pre&gt;
