&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;@model IKLE.Model.ProductModel.AdminProductDetailModel
&amp;lt;div &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;editor-field&quot;&lt;/span&gt;&amp;gt;
  @Html.LabelFor(model =&amp;gt; model.fkConfigChoiceCategorySizeId)
  @Html.DropDownListFor(model =&amp;gt; model.fkConfigChoiceCategorySizeId, Model.sizeList, &lt;span class=&quot;hljs-string&quot;&gt;&quot;--Select Size--&quot;&lt;/span&gt;)
