&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable constant_&quot;&gt;WURFL&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;is_mobile&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;//dostuff();&lt;/span&gt;
}
