        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; clickedCell = $(e.&lt;span class=&quot;hljs-property&quot;&gt;target&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;closest&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;td&quot;&lt;/span&gt;);
        navigator.&lt;span class=&quot;hljs-property&quot;&gt;clipboard&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;writeText&lt;/span&gt;(clickedCell.&lt;span class=&quot;hljs-title function_&quot;&gt;text&lt;/span&gt;());
        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(clickedCell.&lt;span class=&quot;hljs-title function_&quot;&gt;text&lt;/span&gt;());
    });&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;snippet-code-html lang-html s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;src&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;
