                     &lt;span class=&quot;hljs-variable&quot;&gt;$date&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; years, &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; months, &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot;days&quot;&lt;/span&gt;;
