&lt;p&gt;View Hours and Minuts and Seconds..&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&quot;2008-11-01 22:45:00&quot;&lt;/span&gt;; 

&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&quot;2009-12-04 13:44:01&quot;&lt;/span&gt;; 

&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;abs&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;) - &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;)); 

&lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt;   = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; / (&lt;span class=&quot;hljs-number&quot;&gt;365&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;)); 
&lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;  = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;365&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;) / (&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;)); 
&lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt;    = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;365&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;)/ (&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;));

&lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt;   = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;365&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;)/ (&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;)); 

&lt;span class=&quot;hljs-variable&quot;&gt;$minuts&lt;/span&gt;  = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;365&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;)/ &lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;); 

&lt;span class=&quot;hljs-variable&quot;&gt;$seconds&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;365&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$minuts&lt;/span&gt;*&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;)); 

&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;printf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;%d years, %d months, %d days, %d hours, %d minuts\n, %d seconds\n&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$minuts&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$seconds&lt;/span&gt;); 
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Take a look at the following link. This is the best answer I&apos;ve found so far.. :)&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;dateDiff&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$d1&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$d2&lt;/span&gt;&lt;/span&gt;) &lt;/span&gt;{

    &lt;span class=&quot;hljs-comment&quot;&gt;// Return the number of days between the two dates:    &lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;round&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;abs&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$d1&lt;/span&gt;) - &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$d2&lt;/span&gt;))/&lt;span class=&quot;hljs-number&quot;&gt;86400&lt;/span&gt;);

} &lt;span class=&quot;hljs-comment&quot;&gt;// end function dateDiff&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;blockquote&gt;
  &lt;p&gt;It doesn&apos;t matter which date is earlier or later when you pass in the
  date parameters. The function uses the PHP ABS() absolute value to
  always return a postive number as the number of days between the two
  dates.&lt;/p&gt;

  &lt;p&gt;Keep in mind that the number of days between the two dates is NOT
  inclusive of both dates. So if you are looking for the number of days
  represented by all the dates between and including the dates entered,
  you will need to add one (1) to the result of this function.&lt;/p&gt;

  &lt;p&gt;For example, the difference (as returned by the above function)
  between 2013-02-09 and 2013-02-14 is 5. But the number of days or
  dates represented by the date range 2013-02-09 - 2013-02-14 is 6.&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;&lt;a href=&quot;http://www.bizinfosys.com/php/date-difference.html&quot; rel=&quot;noreferrer&quot;&gt;http://www.bizinfosys.com/php/date-difference.html&lt;/a&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;&amp;lt;?php&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$today&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2011-02-03 00:00:00&quot;&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$myBirthDate&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;1964-10-30 00:00:00&quot;&lt;/span&gt;);
    &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;printf&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Days since my birthday: &quot;&lt;/span&gt;, (&lt;span class=&quot;hljs-variable&quot;&gt;$today&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$myBirthDate&lt;/span&gt;)/&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;/&lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;/&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;);
&lt;span class=&quot;hljs-meta&quot;&gt;?&amp;gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I voted for &lt;a href=&quot;https://stackoverflow.com/users/474405/jurka&quot;&gt;jurka&lt;/a&gt;&apos;s &lt;a href=&quot;https://stackoverflow.com/questions/676824/how-to-calculate-the-date-difference-between-2-dates-using-php/3923228#3923228&quot;&gt;answer&lt;/a&gt; as that&apos;s my favorite, but I have a pre-php.5.3 version...&lt;/p&gt;

&lt;p&gt;I found myself working on a similar problem - which is how I got to this question in the first place - but just needed a difference in hours.  But my function solved this one pretty nicely as well and I don&apos;t have anywhere in my own library to keep it where it won&apos;t get lost and forgotten, so... hope this is useful to someone.&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;/**
 *
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; DateTime $oDate1
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@param&lt;/span&gt; DateTime $oDate2
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@return&lt;/span&gt; array 
 */&lt;/span&gt;
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;date_diff_array&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;DateTime &lt;span class=&quot;hljs-variable&quot;&gt;$oDate1&lt;/span&gt;, DateTime &lt;span class=&quot;hljs-variable&quot;&gt;$oDate2&lt;/span&gt;&lt;/span&gt;) &lt;/span&gt;{
    &lt;span class=&quot;hljs-variable&quot;&gt;$aIntervals&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;month&apos;&lt;/span&gt;  =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;week&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;day&apos;&lt;/span&gt;    =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;hour&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;minute&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;second&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,
    );

    &lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$aIntervals&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$sInterval&lt;/span&gt; =&amp;gt; &amp;amp;&lt;span class=&quot;hljs-variable&quot;&gt;$iInterval&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$oDate1&lt;/span&gt; &amp;lt;= &lt;span class=&quot;hljs-variable&quot;&gt;$oDate2&lt;/span&gt;){ 
            &lt;span class=&quot;hljs-variable&quot;&gt;$oDate1&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;modify&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;+1 &apos;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$sInterval&lt;/span&gt;);
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$oDate1&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$oDate2&lt;/span&gt;) {
                &lt;span class=&quot;hljs-variable&quot;&gt;$oDate1&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;modify&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;-1 &apos;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$sInterval&lt;/span&gt;);
                &lt;span class=&quot;hljs-keyword&quot;&gt;break&lt;/span&gt;;
            } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
                &lt;span class=&quot;hljs-variable&quot;&gt;$iInterval&lt;/span&gt;++;
            }
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$aIntervals&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And the test: &lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$oDate&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;();
&lt;span class=&quot;hljs-variable&quot;&gt;$oDate&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;modify&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;+111402189 seconds&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;var_dump&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$oDate&lt;/span&gt;);
&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;var_dump&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff_array&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(), &lt;span class=&quot;hljs-variable&quot;&gt;$oDate&lt;/span&gt;));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And the result:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;object&lt;/span&gt;(DateTime)[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]
  &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;date&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;string&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;2014-04-29 18:52:51&apos;&lt;/span&gt; (length=&lt;span class=&quot;hljs-number&quot;&gt;19&lt;/span&gt;)
  &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;timezone_type&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;timezone&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;string&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;America/New_York&apos;&lt;/span&gt; (length=&lt;span class=&quot;hljs-number&quot;&gt;16&lt;/span&gt;)

&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;month&apos;&lt;/span&gt;  =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;week&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;day&apos;&lt;/span&gt;    =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;hour&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;minute&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;
  &lt;span class=&quot;hljs-string&quot;&gt;&apos;second&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I got the original idea from &lt;a href=&quot;http://www.php.net/manual/en/datetime.diff.php#97810&quot; rel=&quot;nofollow noreferrer&quot;&gt;here&lt;/a&gt;, which I modified for my uses (and I hope my modification will show on that page as well).&lt;/p&gt;

&lt;p&gt;You can very easily remove intervals you don&apos;t want (say &quot;week&quot;) by removing them from the &lt;code&gt;$aIntervals&lt;/code&gt; array, or maybe adding an &lt;code&gt;$aExclude&lt;/code&gt; parameter, or just filter them out when you output the string.&lt;/p&gt;

&lt;p&gt;I don&apos;t know if you are using a PHP framework or not, but a lot of PHP frameworks have date/time libraries and helpers to help keep you from reinventing the wheel.&lt;/p&gt;

&lt;p&gt;For example CodeIgniter has the &lt;code&gt;timespan()&lt;/code&gt; function. Simply input two Unix timestamps and it will automatically generate a result like this:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; Year, &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt; Months, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; Weeks, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; Days, &lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt; Hours, &lt;span class=&quot;hljs-number&quot;&gt;16&lt;/span&gt; Minutes
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;a href=&quot;http://codeigniter.com/user_guide/helpers/date_helper.html&quot; rel=&quot;noreferrer&quot;&gt;http://codeigniter.com/user_guide/helpers/date_helper.html&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Here is the runnable code&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2007-03-24&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2009-06-26&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$diff1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;,&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$daysdiff&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$diff1&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;%R%a&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$daysdiff&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;abs&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$daysdiff&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;h3&gt;Use example :&lt;/h3&gt;

&lt;p&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;time_diff_string&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2013-05-01 00:22:35&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;now&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;time_diff_string&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2013-05-01 00:22:35&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;now&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;h3&gt;Output :&lt;/h3&gt;

&lt;p&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; months ago
&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; months, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; weeks, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; days, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; hour, &lt;span class=&quot;hljs-number&quot;&gt;49&lt;/span&gt; minutes, &lt;span class=&quot;hljs-number&quot;&gt;15&lt;/span&gt; seconds ago
&lt;/code&gt;&lt;/pre&gt;

&lt;h3&gt;Function :&lt;/h3&gt;

&lt;p&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;time_diff_string&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$from&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$to&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$full&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;&lt;/span&gt;) &lt;/span&gt;{
    &lt;span class=&quot;hljs-variable&quot;&gt;$from&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$from&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$to&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$to&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$to&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$from&lt;/span&gt;);

    &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;w = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;d / &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;d -= &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;w * &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;;

    &lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;y&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;m&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;month&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;w&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;week&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;day&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;h&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;hour&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;i&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;minute&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;second&apos;&lt;/span&gt;,
    );
    &lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$k&lt;/span&gt; =&amp;gt; &amp;amp;&lt;span class=&quot;hljs-variable&quot;&gt;$v&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$k&lt;/span&gt;) {
            &lt;span class=&quot;hljs-variable&quot;&gt;$v&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$k&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$v&lt;/span&gt; . (&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$k&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; ? &lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt; : &lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            &lt;span class=&quot;hljs-keyword&quot;&gt;unset&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt;[&lt;span class=&quot;hljs-variable&quot;&gt;$k&lt;/span&gt;]);
        }
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!&lt;span class=&quot;hljs-variable&quot;&gt;$full&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;array_slice&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt; ? &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;implode&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;, &apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$string&lt;/span&gt;) . &lt;span class=&quot;hljs-string&quot;&gt;&apos; ago&apos;&lt;/span&gt; : &lt;span class=&quot;hljs-string&quot;&gt;&apos;just now&apos;&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I would prefer to use &lt;code&gt;date_create&lt;/code&gt; and &lt;code&gt;date_diff&lt;/code&gt; objects.&lt;/p&gt;

&lt;p&gt;Code:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2007-03-24&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2009-06-26&quot;&lt;/span&gt;);

&lt;span class=&quot;hljs-variable&quot;&gt;$dateDifference&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;)-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;%y years, %m months and %d days&apos;&lt;/span&gt;);

&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$dateDifference&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Output:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; years, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; months &lt;span class=&quot;hljs-keyword&quot;&gt;and&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; days
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;For more info read &lt;a href=&quot;http://php.net/manual/en/function.date-diff.php&quot; rel=&quot;noreferrer&quot;&gt;PHP &lt;code&gt;date_diff&lt;/code&gt; manual&lt;/a&gt;&lt;/p&gt;

&lt;blockquote&gt;
  &lt;p&gt;According to manual &lt;code&gt;date_diff&lt;/code&gt; is an alias of
  &lt;a href=&quot;http://php.net/manual/en/datetime.diff.php&quot; rel=&quot;noreferrer&quot;&gt;DateTime::diff()&lt;/a&gt;&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;I have some simple logic for that:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;&amp;lt;?php&lt;/span&gt;
    &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;per_days_diff&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2011-12-12&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&apos;2011-12-29&apos;&lt;/span&gt;)
    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;per_days_diff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$start_date&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$end_date&lt;/span&gt;&lt;/span&gt;) &lt;/span&gt;{
        &lt;span class=&quot;hljs-variable&quot;&gt;$per_days&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
        &lt;span class=&quot;hljs-variable&quot;&gt;$noOfWeek&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
        &lt;span class=&quot;hljs-variable&quot;&gt;$noOfWeekEnd&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
        &lt;span class=&quot;hljs-variable&quot;&gt;$highSeason&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;7&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;8&quot;&lt;/span&gt;);

        &lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$start_date&lt;/span&gt;);
        &lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt; += (&lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt; * &lt;span class=&quot;hljs-number&quot;&gt;3600&lt;/span&gt;);
        &lt;span class=&quot;hljs-variable&quot;&gt;$end_date&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$end_date&lt;/span&gt;);

        &lt;span class=&quot;hljs-variable&quot;&gt;$seassion&lt;/span&gt; = (&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;in_array&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;m&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt;), &lt;span class=&quot;hljs-variable&quot;&gt;$highSeason&lt;/span&gt;))?&lt;span class=&quot;hljs-string&quot;&gt;&quot;2&quot;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;1&quot;&lt;/span&gt;;

        &lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;);

        &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt; &amp;lt;= &lt;span class=&quot;hljs-variable&quot;&gt;$end_date&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt; &amp;lt;= &lt;span class=&quot;hljs-variable&quot;&gt;$end_date&lt;/span&gt;) {
                &lt;span class=&quot;hljs-variable&quot;&gt;$date&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;N&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt;);
                &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;array_push&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;,&lt;span class=&quot;hljs-variable&quot;&gt;$date&lt;/span&gt;);
                &lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;+1 day&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$current_date&lt;/span&gt;);
            }
        }

        &lt;span class=&quot;hljs-variable&quot;&gt;$finalDays&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;array_shift&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;);
        &lt;span class=&quot;hljs-comment&quot;&gt;//print_r($noOfdays);&lt;/span&gt;
        &lt;span class=&quot;hljs-variable&quot;&gt;$weekFirst&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;week&quot;&lt;/span&gt;=&amp;gt;&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(),&lt;span class=&quot;hljs-string&quot;&gt;&quot;weekEnd&quot;&lt;/span&gt;=&amp;gt;&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;());
        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;count&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;); &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;++)
        {
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;[&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;] == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
            {
                &lt;span class=&quot;hljs-comment&quot;&gt;//echo &quot;This is week&quot;;&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;//echo &quot;&amp;lt;br/&amp;gt;&quot;;&lt;/span&gt;
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;[&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;+&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;]==&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;)
                {
                    &lt;span class=&quot;hljs-variable&quot;&gt;$noOfWeek&lt;/span&gt;++;
                    &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;=&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;+&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;;
                }
                &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
                {
                    &lt;span class=&quot;hljs-variable&quot;&gt;$per_days&lt;/span&gt;++;
                }
                &lt;span class=&quot;hljs-comment&quot;&gt;//array_push($weekFirst[&quot;week&quot;],$day);&lt;/span&gt;
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;[&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;]==&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;)
            {
                &lt;span class=&quot;hljs-comment&quot;&gt;//echo &quot;This is weekend&quot;;&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;//echo &quot;&amp;lt;br/&amp;gt;&quot;;&lt;/span&gt;
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$noOfdays&lt;/span&gt;[&lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;+&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;] ==&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;)
                {
                    &lt;span class=&quot;hljs-variable&quot;&gt;$noOfWeekEnd&lt;/span&gt;++;
                    &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$i&lt;/span&gt;+&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;;
                }
                &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
                {
                    &lt;span class=&quot;hljs-variable&quot;&gt;$per_days&lt;/span&gt;++;
                }
                &lt;span class=&quot;hljs-comment&quot;&gt;//echo &quot;After weekend value:- &quot;.$i;&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;//echo &quot;&amp;lt;br/&amp;gt;&quot;;&lt;/span&gt;
            }
            &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
            {
                &lt;span class=&quot;hljs-variable&quot;&gt;$per_days&lt;/span&gt;++;
            }
        }

        &lt;span class=&quot;hljs-comment&quot;&gt;/*echo $noOfWeek;
          echo &quot;&amp;lt;br/&amp;gt;&quot;;
          echo $noOfWeekEnd;
          echo &quot;&amp;lt;br/&amp;gt;&quot;;
          print_r($per_days);
          echo &quot;&amp;lt;br/&amp;gt;&quot;;
          print_r($weekFirst);
        */&lt;/span&gt;

        &lt;span class=&quot;hljs-variable&quot;&gt;$duration&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;weeks&quot;&lt;/span&gt;=&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$noOfWeek&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;weekends&quot;&lt;/span&gt;=&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$noOfWeekEnd&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;perDay&quot;&lt;/span&gt;=&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$per_days&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;seassion&quot;&lt;/span&gt;=&amp;gt;&lt;span class=&quot;hljs-variable&quot;&gt;$seassion&lt;/span&gt;);
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$duration&lt;/span&gt;;
      &lt;span class=&quot;hljs-meta&quot;&gt;?&amp;gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Try this very simple answer using &lt;a href=&quot;https://www.php.net/manual/en/function.date-diff.php&quot; rel=&quot;noreferrer&quot;&gt;&lt;strong&gt;date_diff()&lt;/strong&gt;&lt;/a&gt;, this is tested.&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2017-11-27&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2018-12-29&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;,&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;%m months&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;%y years&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;%d days&quot;&lt;/span&gt;);

&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; .&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt;.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;the output is:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; years &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; months &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; days
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can use the&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;getdate&lt;/span&gt;()
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;function which returns an array containing all elements of the date/time supplied:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;abs&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$endDate&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$startDate&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$my_t&lt;/span&gt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;getdate&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;);
&lt;span class=&quot;hljs-keyword&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&lt;span class=&quot;hljs-subst&quot;&gt;$my_t&lt;/span&gt;[year] years, &lt;span class=&quot;hljs-subst&quot;&gt;$my_t&lt;/span&gt;[month] months and &lt;span class=&quot;hljs-subst&quot;&gt;$my_t&lt;/span&gt;[mday] days&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If your start and end dates are in string format then use&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$startDate&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$startDateStr&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$endDate&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$endDateStr&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;before the above code&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// If you just want to see the year difference then use this function.&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// Using the logic I&apos;ve created you may also create month and day difference&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// which I did not provide here so you may have the efforts to use your brain.&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// :)&lt;/span&gt;
&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos;2009-01-01&apos;&lt;/span&gt;;
&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos;2010-01-01&apos;&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;getYearDifference&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;,&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;);
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;getYearDifference&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;=strtotime(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;&lt;/span&gt;),&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;=strtotime(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;&lt;/span&gt;)&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class=&quot;hljs-variable&quot;&gt;$year&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;+1 year&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;)){
        ++&lt;span class=&quot;hljs-variable&quot;&gt;$year&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$year&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This is my function. Required PHP &amp;gt;= 5.3.4. It use DateTime class. Very fast, quick and can do the difference between two dates or even the so called &quot;time since&quot;.&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;function_exists&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;grk_Datetime_Since&apos;&lt;/span&gt;) === &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;){
    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;grk_Datetime_Since&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$Prefix&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$Suffix&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos; ago&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;)&lt;/span&gt;)&lt;/span&gt;{
        &lt;span class=&quot;hljs-comment&quot;&gt;#   Est-ce qu&apos;on calcul jusqu&apos;à un moment précis ? Probablement pas, on utilise maintenant&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;empty&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;) === &lt;span class=&quot;hljs-literal&quot;&gt;TRUE&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;time&lt;/span&gt;();
        }

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On va s&apos;assurer que $From est numérique&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;is_int&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt;) === &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt;);
        };

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On va s&apos;assurer que $To est numérique&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;is_int&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;) === &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;);
        }

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On a une erreur ?&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt; === &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt; OR &lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt; === -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; OR &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt; === &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt; OR &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt; === -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;;
        }

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On va créer deux objets de date&lt;/span&gt;
        &lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(@&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Y-m-d H:i:s&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt;), &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTimeZone&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GMT&apos;&lt;/span&gt;));
        &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;   = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(@&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Y-m-d H:i:s&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;), &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTimeZone&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GMT&apos;&lt;/span&gt;));

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On va calculer la différence entre $From et $To&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$From&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$To&lt;/span&gt;)) === &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;;
        }

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On va merger le tableau des noms (par défaut, anglais)&lt;/span&gt;
        &lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;array_merge&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;      =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;years&apos;&lt;/span&gt;     =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;years&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;month&apos;&lt;/span&gt;     =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;month&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;months&apos;&lt;/span&gt;    =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;months&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;week&apos;&lt;/span&gt;      =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;week&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;weeks&apos;&lt;/span&gt;     =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;weeks&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;day&apos;&lt;/span&gt;       =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;day&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;days&apos;&lt;/span&gt;      =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;days&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;hour&apos;&lt;/span&gt;      =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;hour&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;hours&apos;&lt;/span&gt;     =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;hours&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;minute&apos;&lt;/span&gt;    =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;minute&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;minutes&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;minutes&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;second&apos;&lt;/span&gt;    =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;second&apos;&lt;/span&gt;,
            &lt;span class=&quot;hljs-string&quot;&gt;&apos;seconds&apos;&lt;/span&gt;   =&amp;gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;seconds&apos;&lt;/span&gt;
        ), &lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;);

        &lt;span class=&quot;hljs-comment&quot;&gt;#   On va créer la chaîne maintenant&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;y &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;y.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;years&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;y == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;year&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;m &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;m.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;months&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;m == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;month&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;d &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;ceil&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;d/&lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;).&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;weeks&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;d == &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;week&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;d &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;d.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;days&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;d == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;day&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;h &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;h.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;hours&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;h == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;hour&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;i &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;i.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;minutes&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;i == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;minute&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;s &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$Diff&lt;/span&gt;-&amp;gt;s.&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;seconds&apos;&lt;/span&gt;];
        } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            &lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;1 &apos;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Words&lt;/span&gt;[&lt;span class=&quot;hljs-string&quot;&gt;&apos;second&apos;&lt;/span&gt;];
        }

        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$Prefix&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Text&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$Suffix&lt;/span&gt;;
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;you can always use the following function that can return the age in years and months (ie. 1 Year 4 Months)&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;getAge&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$dob&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$age_at_date&lt;/span&gt;&lt;/span&gt;)
&lt;/span&gt;{  
    &lt;span class=&quot;hljs-variable&quot;&gt;$d1&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$dob&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$d2&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$age_at_date&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$d2&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$d1&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt;-&amp;gt;y;
    &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt;-&amp;gt;m;

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt;.&lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;.months;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;or if you want the age to be calculated at the current date, you can use&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;getAge&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$dob&lt;/span&gt;&lt;/span&gt;)
&lt;/span&gt;{  
    &lt;span class=&quot;hljs-variable&quot;&gt;$d1&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$dob&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$d2&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;());
    &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$d2&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$d1&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt;-&amp;gt;y;
    &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt;-&amp;gt;m;

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt;.&lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt;.months;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;use this function&lt;/p&gt;
&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;//function Diff between Dates&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;//////////////////////////////////////////////////////////////////////&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;//PARA: Date Should In YYYY-MM-DD Format&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;//RESULT FORMAT:&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%y Year %m Month %d Day %h Hours %i Minute %s Seconds&apos; =&amp;gt;  1 Year 3 Month 14 Day 11 Hours 49 Minute 36 Seconds&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%y Year %m Month %d Day&apos;                       =&amp;gt;  1 Year 3 Month 14 Days&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%m Month %d Day&apos;                                     =&amp;gt;  3 Month 14 Day&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%d Day %h Hours&apos;                                   =&amp;gt;  14 Day 11 Hours&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%d Day&apos;                                                 =&amp;gt;  14 Days&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%h Hours %i Minute %s Seconds&apos;         =&amp;gt;  11 Hours 49 Minute 36 Seconds&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%i Minute %s Seconds&apos;                           =&amp;gt;  49 Minute 36 Seconds&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%h Hours                                          =&amp;gt;  11 Hours&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;// &apos;%a Days                                                =&amp;gt;  468 Days&lt;/span&gt;
&lt;span class=&quot;hljs-comment&quot;&gt;//////////////////////////////////////////////////////////////////////&lt;/span&gt;
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;dateDifference&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date_1&lt;/span&gt; , &lt;span class=&quot;hljs-variable&quot;&gt;$date_2&lt;/span&gt; , &lt;span class=&quot;hljs-variable&quot;&gt;$differenceFormat&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;%a&apos;&lt;/span&gt; &lt;/span&gt;)
&lt;/span&gt;{
    &lt;span class=&quot;hljs-variable&quot;&gt;$datetime1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date_1&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$datetime2&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date_2&lt;/span&gt;);

    &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$datetime1&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$datetime2&lt;/span&gt;);

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$differenceFormat&lt;/span&gt;);

}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;only set parameter &lt;strong&gt;$differenceFormat&lt;/strong&gt;  As your need
example I want Diff between to years with months and days your age&lt;/p&gt;
&lt;p&gt;dateDifference(&lt;strong&gt;date(&apos;Y-m-d&apos;)&lt;/strong&gt; , &lt;strong&gt;$date&lt;/strong&gt; , &apos;&lt;strong&gt;%y&lt;/strong&gt; &lt;strong&gt;%m&lt;/strong&gt; &lt;strong&gt;%d&lt;/strong&gt;&apos;)&lt;/p&gt;
&lt;p&gt;or other format&lt;/p&gt;
&lt;p&gt;dateDifference(&lt;strong&gt;date(&apos;Y-m-d&apos;)&lt;/strong&gt; , &lt;strong&gt;$date&lt;/strong&gt; , &apos;&lt;strong&gt;%y&lt;/strong&gt;-&lt;strong&gt;%m&lt;/strong&gt;-&lt;strong&gt;%d&lt;/strong&gt;&apos;)&lt;/p&gt;

&lt;p&gt;This will try to detect whether a timestamp was given or not, and will also return future dates/times as negative values:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;&amp;lt;?php&lt;/span&gt;

&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;time_diff&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$start&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$end&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;NULL&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$convert_to_timestamp&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;FALSE&lt;/span&gt;&lt;/span&gt;) &lt;/span&gt;{
  &lt;span class=&quot;hljs-comment&quot;&gt;// If $convert_to_timestamp is not explicitly set to TRUE,&lt;/span&gt;
  &lt;span class=&quot;hljs-comment&quot;&gt;// check to see if it was accidental:&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$convert_to_timestamp&lt;/span&gt; || !&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;is_numeric&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$start&lt;/span&gt;)) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// If $convert_to_timestamp is TRUE, convert to timestamp:&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$timestamp_start&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$start&lt;/span&gt;);
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Otherwise, leave it as a timestamp:&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$timestamp_start&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$start&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-comment&quot;&gt;// Same as above, but make sure $end has actually been overridden with a non-null,&lt;/span&gt;
  &lt;span class=&quot;hljs-comment&quot;&gt;// non-empty, non-numeric value:&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;is_null&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$end&lt;/span&gt;) &amp;amp;&amp;amp; (!&lt;span class=&quot;hljs-keyword&quot;&gt;empty&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$end&lt;/span&gt;) &amp;amp;&amp;amp; !&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;is_numeric&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$end&lt;/span&gt;))) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$timestamp_end&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strtotime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$end&lt;/span&gt;);
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
    &lt;span class=&quot;hljs-comment&quot;&gt;// If $end is NULL or empty and non-numeric value, assume the end time desired&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;// is the current time (useful for age, etc):&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$timestamp_end&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;time&lt;/span&gt;();
  }
  &lt;span class=&quot;hljs-comment&quot;&gt;// Regardless, set the start and end times to an integer:&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$start_time&lt;/span&gt; = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$timestamp_start&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$end_time&lt;/span&gt; = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$timestamp_end&lt;/span&gt;;

  &lt;span class=&quot;hljs-comment&quot;&gt;// Assign these values as the params for $then and $now:&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$start_time_var&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;start_time&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$end_time_var&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;end_time&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-comment&quot;&gt;// Use this to determine if the output is positive (time passed) or negative (future):&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;

  &lt;span class=&quot;hljs-comment&quot;&gt;// If the end time is at a later time than the start time, do the opposite:&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$end_time&lt;/span&gt; &amp;lt;= &lt;span class=&quot;hljs-variable&quot;&gt;$start_time&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$start_time_var&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;end_time&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$end_time_var&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;start_time&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt; = -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-comment&quot;&gt;// Convert everything to the proper format, and do some math:&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Y-m-d H:i:s&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$$start_time_var&lt;/span&gt;));
  &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Y-m-d H:i:s&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$$end_time_var&lt;/span&gt;));

  &lt;span class=&quot;hljs-variable&quot;&gt;$years_then&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Y&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$years_now&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Y&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$years_now&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$years_then&lt;/span&gt;;

  &lt;span class=&quot;hljs-variable&quot;&gt;$months_then&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;m&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$months_now&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;m&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$months_now&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$months_then&lt;/span&gt;;

  &lt;span class=&quot;hljs-variable&quot;&gt;$days_then&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$days_now&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;d&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$days_now&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$days_then&lt;/span&gt;;

  &lt;span class=&quot;hljs-variable&quot;&gt;$hours_then&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;H&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$hours_now&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;H&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$hours_now&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$hours_then&lt;/span&gt;;

  &lt;span class=&quot;hljs-variable&quot;&gt;$minutes_then&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;i&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$minutes_now&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;i&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$minutes&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$minutes_now&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$minutes_then&lt;/span&gt;;

  &lt;span class=&quot;hljs-variable&quot;&gt;$seconds_then&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$seconds_now&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt;);
  &lt;span class=&quot;hljs-variable&quot;&gt;$seconds&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$seconds_now&lt;/span&gt; - &lt;span class=&quot;hljs-variable&quot;&gt;$seconds_then&lt;/span&gt;;

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$seconds&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$minutes&lt;/span&gt; -= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$seconds&lt;/span&gt; += &lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$minutes&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt; -= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$minutes&lt;/span&gt; += &lt;span class=&quot;hljs-number&quot;&gt;60&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; -= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt; += &lt;span class=&quot;hljs-number&quot;&gt;24&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$months_now&lt;/span&gt; - &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$months_now&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$years_now&lt;/span&gt; -= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;12&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-comment&quot;&gt;// &quot;Thirty days hath September, April, June, and November&quot; ;)&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt; || &lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; || &lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt; || &lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;11&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$days_last_month&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$months_last&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Factor in leap years:&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ((&lt;span class=&quot;hljs-variable&quot;&gt;$years_now&lt;/span&gt; % &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;) == &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
      &lt;span class=&quot;hljs-variable&quot;&gt;$days_last_month&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;29&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
      &lt;span class=&quot;hljs-variable&quot;&gt;$days_last_month&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;28&lt;/span&gt;;
    }
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
    &lt;span class=&quot;hljs-variable&quot;&gt;$days_last_month&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;31&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; -= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; += &lt;span class=&quot;hljs-variable&quot;&gt;$days_last_month&lt;/span&gt;;
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; &amp;lt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; -= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; += &lt;span class=&quot;hljs-number&quot;&gt;12&lt;/span&gt;;
  }

  &lt;span class=&quot;hljs-comment&quot;&gt;// Finally, multiply each value by either 1 (in which case it will stay the same),&lt;/span&gt;
  &lt;span class=&quot;hljs-comment&quot;&gt;// or by -1 (in which case it will become negative, for future dates).&lt;/span&gt;
  &lt;span class=&quot;hljs-comment&quot;&gt;// Note: 0 * 1 == 0 * -1 == 0&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;stdClass&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;-&amp;gt;years = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$years&lt;/span&gt; * &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;-&amp;gt;months = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$months&lt;/span&gt; * &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;-&amp;gt;days = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$days&lt;/span&gt; * &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;-&amp;gt;hours = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$hours&lt;/span&gt; * &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;-&amp;gt;minutes = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$minutes&lt;/span&gt; * &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;-&amp;gt;seconds = (&lt;span class=&quot;hljs-keyword&quot;&gt;int&lt;/span&gt;) &lt;span class=&quot;hljs-variable&quot;&gt;$seconds&lt;/span&gt; * &lt;span class=&quot;hljs-variable&quot;&gt;$pos_neg&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$out&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Example usage:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;&amp;lt;?php&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$birthday&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;June 2, 1971&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$check_age_for_this_date&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;June 3, 1999 8:53pm&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;time_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$birthday&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$check_age_for_this_date&lt;/span&gt;)-&amp;gt;years;
  &lt;span class=&quot;hljs-keyword&quot;&gt;print&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$age&lt;/span&gt;;&lt;span class=&quot;hljs-comment&quot;&gt;// 28&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Or:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-meta&quot;&gt;&amp;lt;?php&lt;/span&gt;
  &lt;span class=&quot;hljs-variable&quot;&gt;$christmas_2020&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;December 25, 2020&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-variable&quot;&gt;$countdown&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;time_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$christmas_2020&lt;/span&gt;);
  &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;print_r&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$countdown&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I found your article on the following page, which contains a number of references for &lt;a href=&quot;http://en.wikipedia.org/wiki/PHP&quot; rel=&quot;noreferrer&quot;&gt;PHP&lt;/a&gt; date time calculations.&lt;/p&gt;

&lt;p&gt;Calculate the difference between two Dates (and time) using PHP. The following page provides a range of different methods (7 in total) for performing date / time calculations using PHP, to determine the difference in time (hours, munites), days, months or years between two dates.&lt;/p&gt;

&lt;p&gt;See &lt;a href=&quot;http://forums.webmasterhub.net/viewtopic.php?f=23&amp;amp;t=1831&quot; rel=&quot;noreferrer&quot;&gt;PHP Date Time  7 Methods to Calculate the Difference between 2 dates&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;For php version &amp;gt;=5.3 : Create two date objects and then use &lt;code&gt;date_diff()&lt;/code&gt; function. It will return php &lt;a href=&quot;http://php.net/manual/en/class.dateinterval.php&quot; rel=&quot;noreferrer&quot;&gt;DateInterval&lt;/a&gt; object. &lt;a href=&quot;http://php.net/manual/en/function.date-diff.php&quot; rel=&quot;noreferrer&quot;&gt;see documentation&lt;/a&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2007-03-24&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;2009-06-26&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;=&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;,&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;);
&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;format&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;%R%a days&quot;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;An easy function&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;time_difference&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$time_1&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$time_2&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$limit&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;&lt;/span&gt;)
&lt;/span&gt;{

    &lt;span class=&quot;hljs-variable&quot;&gt;$val_1&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$time_1&lt;/span&gt;);
    &lt;span class=&quot;hljs-variable&quot;&gt;$val_2&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;DateTime&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$time_2&lt;/span&gt;);

    &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$val_1&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$val_2&lt;/span&gt;);

    &lt;span class=&quot;hljs-variable&quot;&gt;$output&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;array&lt;/span&gt;(
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;year&quot;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;y,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;month&quot;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;m,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;day&quot;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;d,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;hour&quot;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;h,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;minute&quot;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;i,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;second&quot;&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;s
    );

    &lt;span class=&quot;hljs-variable&quot;&gt;$return&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;foreach&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$output&lt;/span&gt; AS &lt;span class=&quot;hljs-variable&quot;&gt;$key&lt;/span&gt; =&amp;gt; &lt;span class=&quot;hljs-variable&quot;&gt;$value&lt;/span&gt;) {

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$value&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
            &lt;span class=&quot;hljs-variable&quot;&gt;$return&lt;/span&gt; .= &lt;span class=&quot;hljs-variable&quot;&gt;$value&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$key&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; &quot;&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;elseif&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$value&lt;/span&gt; &amp;gt;= &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;)
            &lt;span class=&quot;hljs-variable&quot;&gt;$return&lt;/span&gt; .= &lt;span class=&quot;hljs-variable&quot;&gt;$value&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot; &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$key&lt;/span&gt; . &lt;span class=&quot;hljs-string&quot;&gt;&quot;s &quot;&lt;/span&gt;;

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$key&lt;/span&gt; == &lt;span class=&quot;hljs-variable&quot;&gt;$limit&lt;/span&gt;)
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;trim&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$return&lt;/span&gt;);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;trim&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$return&lt;/span&gt;);
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Use like&lt;/p&gt;

&lt;p&gt;&lt;code&gt;echo time_difference ($time_1, $time_2, &quot;day&quot;);&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;Will return like &lt;code&gt;2 years 8 months 2 days&lt;/code&gt;&lt;/p&gt;

&lt;p&gt;You can also use following code to return date diff by round fractions up
    $date1 = $duedate; // assign due date
    echo $date2 = date(&quot;Y-m-d&quot;); // current date 
    $ts1 = strtotime($date1); 
    $ts2 = strtotime($date2);
    $seconds_diff = $ts1 - $ts2;
    echo $datediff = ceil(($seconds_diff/3600)/24); // return in days&lt;/p&gt;

&lt;p&gt;If you use floor method of php instead of ceil it will return you the round fraction down. Please check the difference here, some times if your staging servers timezone is different then the live site time zone in that case you may get different results so change the conditions accordingly.&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2007-03-24&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2009-06-26&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date1&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$date2&lt;/span&gt;);
&lt;span class=&quot;hljs-keyword&quot;&gt;echo&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&quot;difference : &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;y . &lt;span class=&quot;hljs-string&quot;&gt;&quot; years, &quot;&lt;/span&gt; . &lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;m.&lt;span class=&quot;hljs-string&quot;&gt;&quot; months, &quot;&lt;/span&gt;.&lt;span class=&quot;hljs-variable&quot;&gt;$interval&lt;/span&gt;-&amp;gt;d.&lt;span class=&quot;hljs-string&quot;&gt;&quot; days &quot;&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I had the same problem with PHP 5.2 and solved it with MySQL. Might not be exactly what you&apos;re looking for, but this will do the trick and return the number of days:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$datediff_q&lt;/span&gt; = &lt;span class=&quot;hljs-variable&quot;&gt;$dbh&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;prepare&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;SELECT DATEDIFF(:date2, :date1)&quot;&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$datediff_q&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;bindValue&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;:date1&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;2007-03-24&apos;&lt;/span&gt;, PDO::&lt;span class=&quot;hljs-variable constant_&quot;&gt;PARAM_STR&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$datediff_q&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;bindValue&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;:date2&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;2009-06-26&apos;&lt;/span&gt;, PDO::&lt;span class=&quot;hljs-variable constant_&quot;&gt;PARAM_STR&lt;/span&gt;);
&lt;span class=&quot;hljs-variable&quot;&gt;$datediff&lt;/span&gt; = (&lt;span class=&quot;hljs-variable&quot;&gt;$datediff_q&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;execute&lt;/span&gt;()) ? &lt;span class=&quot;hljs-variable&quot;&gt;$datediff_q&lt;/span&gt;-&amp;gt;&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;fetchColumn&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) : &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;More info here &lt;a href=&quot;http://dev.mysql.com/doc/refman/5.5/en/date-and-time-functions.html#function_datediff&quot; rel=&quot;nofollow&quot;&gt;http://dev.mysql.com/doc/refman/5.5/en/date-and-time-functions.html#function_datediff&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Since everyone is posting code samples, here is another version.&lt;/p&gt;

&lt;p&gt;I wanted a function to display differences from seconds to years (just one unit). For periods over 1 day, I wanted it to rollover at midnight (10am Monday seen from 9am Wednesday is 2 days ago, not 1). And for periods over a month, I wanted the rollover to be on the same day of the month (including for 30/31 day months &amp;amp; leap years).&lt;/p&gt;

&lt;p&gt;This is what I came up with:&lt;/p&gt;

&lt;pre class=&quot;lang-php s-code-block&quot;&gt;&lt;code class=&quot;hljs language-php&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;/**
 * Returns how long ago something happened in the past, showing it
 * as n seconds / minutes / hours / days / weeks / months / years ago.
 *
 * For periods over a day, it rolls over at midnight (so doesn&apos;t depend
 * on current time of day), and it correctly accounts for month-lengths
 * and leap-years (months and years rollover on current day of month).
 *
 * $param string $timestamp in DateTime format
 * $return string description of interval
 */&lt;/span&gt;
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ago&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-variable&quot;&gt;$timestamp&lt;/span&gt;&lt;/span&gt;)
&lt;/span&gt;{
    &lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$timestamp&lt;/span&gt;);

    &lt;span class=&quot;hljs-comment&quot;&gt;// for anything over 1 day, make it rollover on midnight&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$today&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;tomorrow&apos;&lt;/span&gt;); &lt;span class=&quot;hljs-comment&quot;&gt;// ie end of today&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$today&lt;/span&gt;);

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;y &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;y.&lt;span class=&quot;hljs-string&quot;&gt;&apos; year&apos;&lt;/span&gt;.(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;y&amp;gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;?&lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-string&quot;&gt;&apos; ago&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;m &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;m.&lt;span class=&quot;hljs-string&quot;&gt;&apos; month&apos;&lt;/span&gt;.(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;m&amp;gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;?&lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-string&quot;&gt;&apos; ago&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-variable&quot;&gt;$diffW&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;floor&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;d / &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$diffW&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$diffW&lt;/span&gt;.&lt;span class=&quot;hljs-string&quot;&gt;&apos; week&apos;&lt;/span&gt;.(&lt;span class=&quot;hljs-variable&quot;&gt;$diffW&lt;/span&gt;&amp;gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;?&lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-string&quot;&gt;&apos; ago&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;d &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;d.&lt;span class=&quot;hljs-string&quot;&gt;&apos; day&apos;&lt;/span&gt;.(&lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt;-&amp;gt;d&amp;gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;?&lt;span class=&quot;hljs-string&quot;&gt;&apos;s&apos;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-string&quot;&gt;&apos; ago&apos;&lt;/span&gt;;

    &lt;span class=&quot;hljs-comment&quot;&gt;// for anything less than 1 day, base it off &apos;now&apos;&lt;/span&gt;
    &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_create&lt;/span&gt;();
    &lt;span class=&quot;hljs-variable&quot;&gt;$diff&lt;/span&gt; = &lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;date_diff&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$then&lt;/span&gt;, &lt;span class=&quot;hljs-variable&quot;&gt;$now&lt;/span&gt;);

