    &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(x) &lt;span class=&quot;hljs-comment&quot;&gt;#print(eval(x))&lt;/span&gt;
&lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(datetime.now())

&lt;span class=&quot;hljs-comment&quot;&gt;#when using eval(int)&lt;/span&gt;
