           &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;   
      }
      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strpos&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;-&apos;&lt;/span&gt;) !== &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;) {
           &lt;span class=&quot;hljs-variable&quot;&gt;$divider&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;-&apos;&lt;/span&gt;;
      } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-title function_ invoke__&quot;&gt;strpos&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;$date&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;/&apos;&lt;/span&gt;) !== &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;) {
           &lt;span class=&quot;hljs-variable&quot;&gt;$divider&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;/&apos;&lt;/span&gt;;
