&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;    Red   &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; Yellow   &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;
&lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;   Blue   &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;
