&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-type&quot;&gt;doubleouble&lt;/span&gt;  &lt;span class=&quot;hljs-variable&quot;&gt;BMI&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; (&lt;span class=&quot;hljs-type&quot;&gt;double&lt;/span&gt;) weight / ((&lt;span class=&quot;hljs-type&quot;&gt;double&lt;/span&gt;)height * (&lt;span class=&quot;hljs-type&quot;&gt;double&lt;/span&gt;)height);
