&lt;p&gt;Use this tool to generate a class based in your json:&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://json2csharp.com/&quot; rel=&quot;noreferrer&quot;&gt;http://json2csharp.com/&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;And then use the class to deserialize your json. Example:&lt;/p&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;Account&lt;/span&gt;
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; Email { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;bool&lt;/span&gt; Active { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; DateTime CreatedDate { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; IList&amp;lt;&lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt;&amp;gt; Roles { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }
}


&lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; json = &lt;span class=&quot;hljs-string&quot;&gt;@&quot;{
  &apos;Email&apos;: &apos;james@example.com&apos;,
  &apos;Active&apos;: true,
  &apos;CreatedDate&apos;: &apos;2013-01-20T00:00:00Z&apos;,
  &apos;Roles&apos;: [
    &apos;User&apos;,
    &apos;Admin&apos;
  ]
}&quot;&lt;/span&gt;;

Account account = JsonConvert.DeserializeObject&amp;lt;Account&amp;gt;(json);

Console.WriteLine(account.Email);
&lt;span class=&quot;hljs-comment&quot;&gt;// james@example.com&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;References:
&lt;a href=&quot;https://forums.asp.net/t/1992996.aspx?Nested+Json+Deserialization+to+C+object+and+using+that+object&quot; rel=&quot;noreferrer&quot;&gt;https://forums.asp.net/t/1992996.aspx?Nested+Json+Deserialization+to+C+object+and+using+that+object&lt;/a&gt;
&lt;a href=&quot;https://www.newtonsoft.com/json/help/html/DeserializeObject.htm&quot; rel=&quot;noreferrer&quot;&gt;https://www.newtonsoft.com/json/help/html/DeserializeObject.htm&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;If JSON is dynamic as below&lt;/p&gt;
&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;{
 &lt;span class=&quot;hljs-string&quot;&gt;&quot;Items&quot;&lt;/span&gt;: [{
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;Name&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Apple&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;Price&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;12.3&lt;/span&gt;
    },
    {
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;Name&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Grape&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;Price&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;3.21&lt;/span&gt;
    }
   ],
   &lt;span class=&quot;hljs-string&quot;&gt;&quot;Date&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;21/11/2010&quot;&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Then, Once you install &lt;code&gt;NewtonSoft.Json&lt;/code&gt; from NuGet  and include it in your project, you can serialize it as&lt;/p&gt;
&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; jsonString = &lt;span class=&quot;hljs-string&quot;&gt;&quot;{\&quot;Items\&quot;: [{\&quot;Name\&quot;: \&quot;Apple\&quot;,\&quot;Price\&quot;: 12.3},{\&quot;Name\&quot;: \&quot;Grape\&quot;,\&quot;Price\&quot;: 3.21}],\&quot;Date\&quot;: \&quot;21/11/2010\&quot;}&quot;&lt;/span&gt;;

        &lt;span class=&quot;hljs-built_in&quot;&gt;dynamic&lt;/span&gt; DynamicData = JsonConvert.DeserializeObject(jsonString);

        Console.WriteLine(   DynamicData.Date); &lt;span class=&quot;hljs-comment&quot;&gt;// &quot;21/11/2010&quot;&lt;/span&gt;
        Console.WriteLine(DynamicData.Items.Count); &lt;span class=&quot;hljs-comment&quot;&gt;// 2&lt;/span&gt;
        Console.WriteLine(DynamicData.Items[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].Name); &lt;span class=&quot;hljs-comment&quot;&gt;// &quot;Apple&quot;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Source: &lt;a href=&quot;https://qawithexperts.com/article/c-sharp/how-to-read-json-data-in-c-example-using-console-app-aspnet/174&quot; rel=&quot;noreferrer&quot;&gt;How to read JSON data in C# (Example using Console app &amp;amp; ASP.NET MVC)?&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;The following from the &lt;a href=&quot;https://msdn.microsoft.com/en-us/library/windows/apps/xaml/hh770289.aspx&quot; rel=&quot;nofollow&quot;&gt;msdn&lt;/a&gt; site should I think help provide some native functionality for what you are looking for. Please note it is specified for Windows 8. One such example from the site is listed below.&lt;/p&gt;

&lt;pre class=&quot;lang-html s-code-block&quot;&gt;&lt;code class=&quot;hljs language-xml&quot;&gt;JsonValue jsonValue = JsonValue.Parse(&quot;{\&quot;Width\&quot;: 800, \&quot;Height\&quot;: 600, \&quot;Title\&quot;: \&quot;View from 15th Floor\&quot;, \&quot;IDs\&quot;: [116, 943, 234, 38793]}&quot;);
double width = jsonValue.GetObject().GetNamedNumber(&quot;Width&quot;);
double height = jsonValue.GetObject().GetNamedNumber(&quot;Height&quot;);
string title = jsonValue.GetObject().GetNamedString(&quot;Title&quot;);
JsonArray ids = jsonValue.GetObject().GetNamedArray(&quot;IDs&quot;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;It utilizes the &lt;a href=&quot;https://msdn.microsoft.com/en-us/library/windows/apps/xaml/windows.data.json.aspx&quot; rel=&quot;nofollow&quot;&gt;Windows.Data.JSON&lt;/a&gt; namespace.&lt;/p&gt;

&lt;p&gt;You can use following extentions&lt;/p&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;JsonExtensions&lt;/span&gt;
{
    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; T &lt;span class=&quot;hljs-title&quot;&gt;ToObject&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; jsonText&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; JsonConvert.DeserializeObject&amp;lt;T&amp;gt;(jsonText);
    }

    &lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;ToJson&lt;/span&gt;&amp;lt;&lt;span class=&quot;hljs-title&quot;&gt;T&lt;/span&gt;&amp;gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;this&lt;/span&gt; T obj&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; JsonConvert.SerializeObject(obj);
    } 
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I ended up with a simple class that creates types on the fly, instantiate them and hydrate them, mirroring the structure of the input JSON.&lt;/p&gt;
&lt;p&gt;&lt;a href=&quot;https://i.stack.imgur.com/ScToc.png&quot; rel=&quot;nofollow noreferrer&quot;&gt;&lt;img src=&quot;https://i.stack.imgur.com/ScToc.png&quot; alt=&quot;enter image description here&quot;&gt;&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;You can find it here:&lt;/p&gt;
&lt;p&gt;&lt;a href=&quot;https://github.com/starnutoditopo/JsonToObject&quot; rel=&quot;nofollow noreferrer&quot;&gt;https://github.com/starnutoditopo/JsonToObject&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;I think the best answer that I&apos;ve seen has been @MD_Sayem_Ahmed.&lt;/p&gt;

&lt;p&gt;Your question is &quot;How can I parse Json with C#&quot;, but it seems like you are wanting to decode Json.  If you are wanting to decode it, Ahmed&apos;s answer is good.&lt;/p&gt;

&lt;p&gt;If you are trying to accomplish this in ASP.NET Web Api, the easiest way is to create a data transfer object that holds the data you want to assign:&lt;/p&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;MyDto&lt;/span&gt;{
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; Name{&lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;;}
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; Value{&lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;;}
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You have simply add the application/json header to your request (if you are using Fiddler, for example).
You would then use this in ASP.NET Web API as follows:&lt;/p&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;//controller method -- assuming you want to post and return data&lt;/span&gt;
&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; MyDto &lt;span class=&quot;hljs-title&quot;&gt;Post&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;[FromBody] MyDto myDto&lt;/span&gt;)&lt;/span&gt;{
   MyDto someDto = myDto;
   &lt;span class=&quot;hljs-comment&quot;&gt;/*ASP.NET automatically converts the data for you into this object 
    if you post a json object as follows:
{
    &quot;Name&quot;: &quot;SomeName&quot;,
      &quot;Value&quot;: &quot;SomeValue&quot;
}
*/&lt;/span&gt;
   &lt;span class=&quot;hljs-comment&quot;&gt;//do some stuff&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This helped me a lot when I was working in my Web Api and made my life super easy.&lt;/p&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;         &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; json = &lt;span class=&quot;hljs-string&quot;&gt;@&quot;{
            &apos;Name&apos;: &apos;Wide Web&apos;,
            &apos;Url&apos;: &apos;www.wideweb.com.br&apos;}&quot;&lt;/span&gt;;

        JavaScriptSerializer jsonSerializer = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; JavaScriptSerializer();
        &lt;span class=&quot;hljs-built_in&quot;&gt;dynamic&lt;/span&gt; j = jsonSerializer.Deserialize&amp;lt;&lt;span class=&quot;hljs-built_in&quot;&gt;dynamic&lt;/span&gt;&amp;gt;(json);
        &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; name = j[&lt;span class=&quot;hljs-string&quot;&gt;&quot;Name&quot;&lt;/span&gt;].ToString();
        &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; url = j[&lt;span class=&quot;hljs-string&quot;&gt;&quot;Url&quot;&lt;/span&gt;].ToString();
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; result = controller.ActioName(objParams);
IDictionary&amp;lt;&lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt;, &lt;span class=&quot;hljs-built_in&quot;&gt;object&lt;/span&gt;&amp;gt; data = (IDictionary&amp;lt;&lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt;, &lt;span class=&quot;hljs-built_in&quot;&gt;object&lt;/span&gt;&amp;gt;)&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; System.Web.Routing.RouteValueDictionary(result.Data);
Assert.AreEqual(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Table already exists.&quot;&lt;/span&gt;, data[&lt;span class=&quot;hljs-string&quot;&gt;&quot;Message&quot;&lt;/span&gt;]);
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-cs s-code-block&quot;&gt;&lt;code class=&quot;hljs language-csharp&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;using&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; ms = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; MemoryStream(Encoding.Unicode.GetBytes(user)))
 {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Deserialization from JSON  &lt;/span&gt;
    DataContractJsonSerializer deserializer = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; DataContractJsonSerializer(&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt;(UserListing))
    DataContractJsonSerializer(&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt;(UserListing));
    UserListing response = (UserListing)deserializer.ReadObject(ms);

 }

 &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;UserListing&lt;/span&gt;
 {
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; List&amp;lt;UserList&amp;gt; users { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }      
 }

 &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title&quot;&gt;UserList&lt;/span&gt;
 {
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; FirstName { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; }       
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;string&lt;/span&gt; LastName { &lt;span class=&quot;hljs-keyword&quot;&gt;get&lt;/span&gt;; &lt;span class=&quot;hljs-keyword&quot;&gt;set&lt;/span&gt;; } 
 }
