ot;hljs-property&quot;&gt;windows&lt;/span&gt;={&lt;span class=&quot;hljs-attr&quot;&gt;phone&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(i,r),&lt;span class=&quot;hljs-attr&quot;&gt;tablet&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(j,r),&lt;span class=&quot;hljs-attr&quot;&gt;device&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(i,r)||&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(j,r)},&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;other&lt;/span&gt;={&lt;span class=&quot;hljs-attr&quot;&gt;blackberry&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(k,r),&lt;span class=&quot;hljs-attr&quot;&gt;blackberry10&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(l,r),&lt;span class=&quot;hljs-attr&quot;&gt;opera&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(m,r),&lt;span class=&quot;hljs-attr&quot;&gt;firefox&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(o,r),&lt;span class=&quot;hljs-attr&quot;&gt;chrome&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(n,r),&lt;span class=&quot;hljs-attr&quot;&gt;device&lt;/span&gt;:&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(k,r)||&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(l,r)||&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(m,r)||&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(o,r)||&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(n,r)},&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;seven_inch&lt;/span&gt;=&lt;span class=&quot;hljs-title function_&quot;&gt;q&lt;/span&gt;(p,r),&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;any&lt;/span&gt;=&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;apple&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;device&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;android&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;device&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;windows&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;device&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;other&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;device&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;seven_inch&lt;/span&gt;,&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;phone&lt;/span&gt;=&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;apple&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;phone&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;android&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;phone&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;windows&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;phone&lt;/span&gt;,&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;tablet&lt;/span&gt;=&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;apple&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;tablet&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;android&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;tablet&lt;/span&gt;||&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;windows&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;tablet&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;undefined&quot;&lt;/span&gt;==&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;?&lt;span class=&quot;hljs-attr&quot;&gt;this&lt;/span&gt;:&lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;},s=&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a=&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; r;&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; a.&lt;span class=&quot;hljs-property&quot;&gt;Class&lt;/span&gt;=r,a};&lt;span class=&quot;hljs-string&quot;&gt;&quot;undefined&quot;&lt;/span&gt;!=&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;module&lt;/span&gt;&amp;amp;&amp;amp;&lt;span class=&quot;hljs-variable language_&quot;&gt;module&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;exports&lt;/span&gt;&amp;amp;&amp;amp;&lt;span class=&quot;hljs-string&quot;&gt;&quot;undefined&quot;&lt;/span&gt;==&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;?&lt;span class=&quot;hljs-variable language_&quot;&gt;module&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;exports&lt;/span&gt;=&lt;span class=&quot;hljs-attr&quot;&gt;r&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;undefined&quot;&lt;/span&gt;!=&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;module&lt;/span&gt;&amp;amp;&amp;amp;&lt;span class=&quot;hljs-variable language_&quot;&gt;module&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;exports&lt;/span&gt;&amp;amp;&amp;amp;&lt;span class=&quot;hljs-string&quot;&gt;&quot;undefined&quot;&lt;/span&gt;!=&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;?&lt;span class=&quot;hljs-variable language_&quot;&gt;module&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;exports&lt;/span&gt;=&lt;span class=&quot;hljs-title function_&quot;&gt;s&lt;/span&gt;():&lt;span class=&quot;hljs-string&quot;&gt;&quot;function&quot;&lt;/span&gt;==&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; define&amp;amp;&amp;amp;define.&lt;span class=&quot;hljs-property&quot;&gt;amd&lt;/span&gt;?&lt;span class=&quot;hljs-title function_&quot;&gt;define&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;isMobile&quot;&lt;/span&gt;,[],a.&lt;span class=&quot;hljs-property&quot;&gt;isMobile&lt;/span&gt;=&lt;span class=&quot;hljs-title function_&quot;&gt;s&lt;/span&gt;()):a.&lt;span class=&quot;hljs-property&quot;&gt;isMobile&lt;/span&gt;=&lt;span class=&quot;hljs-title function_&quot;&gt;s&lt;/span&gt;()}(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;);

&lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(isMobile.&lt;span class=&quot;hljs-property&quot;&gt;any&lt;/span&gt; ? &lt;span class=&quot;hljs-string&quot;&gt;&apos;Mobile&apos;&lt;/span&gt; : &lt;span class=&quot;hljs-string&quot;&gt;&apos;Not mobile&apos;&lt;/span&gt;);&lt;/code&gt;&lt;/pre&gt;
