&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; myData = { &lt;span class=&quot;hljs-attr&quot;&gt;code&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;42&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;items&lt;/span&gt;: [{ &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;aaa&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;items&lt;/span&gt;: [{ &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ccc&apos;&lt;/span&gt; }, { &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;ddd&apos;&lt;/span&gt; }] }, { &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;bbb&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;items&lt;/span&gt;: [{ &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;eee&apos;&lt;/span&gt; }, { &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;6&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;fff&apos;&lt;/span&gt; }] }] };

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;findItemById&lt;/span&gt; = (&lt;span class=&quot;hljs-params&quot;&gt;haystack, id&lt;/span&gt;) =&amp;gt; &lt;span class=&quot;hljs-title function_&quot;&gt;objectScan&lt;/span&gt;([&lt;span class=&quot;hljs-string&quot;&gt;&apos;**(^items$).id&apos;&lt;/span&gt;], {
  &lt;span class=&quot;hljs-attr&quot;&gt;abort&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;useArraySelector&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;rtn&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;parent&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;filterFn&lt;/span&gt;: &lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;{ value }&lt;/span&gt;) =&amp;gt;&lt;/span&gt; value === id
})(haystack);

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;findItemById&lt;/span&gt;(myData, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;));
