&lt;p&gt;&lt;em&gt;Disclaimer&lt;/em&gt;: I&apos;m the author of &lt;a href=&quot;https://www.npmjs.com/package/object-scan&quot; rel=&quot;nofollow noreferrer&quot;&gt;object-scan&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;this is how i have done this.&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; groups = [
        {
            &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,
            &lt;span class=&quot;hljs-attr&quot;&gt;title&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Group 1&quot;&lt;/span&gt;,
            &lt;span class=&quot;hljs-attr&quot;&gt;members&lt;/span&gt;:[
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Aftab&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;battry&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;10%&apos;&lt;/span&gt;
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Jamal&quot;&lt;/span&gt;,
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Hamid&quot;&lt;/span&gt;,
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Aqeel&quot;&lt;/span&gt;,
                },
            ]
        },
        {
            &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,
            &lt;span class=&quot;hljs-attr&quot;&gt;title&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Group 2&quot;&lt;/span&gt;,
            &lt;span class=&quot;hljs-attr&quot;&gt;members&lt;/span&gt;:[
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Aftab&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;battry&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;10%&apos;&lt;/span&gt;
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Jamal&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;battry&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;10%&apos;&lt;/span&gt;
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Hamid&quot;&lt;/span&gt;,
                },

            ]
        },
        {
            &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,
            &lt;span class=&quot;hljs-attr&quot;&gt;title&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Group 3&quot;&lt;/span&gt;,
            &lt;span class=&quot;hljs-attr&quot;&gt;members&lt;/span&gt;:[
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Aftab&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;battry&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&apos;10%&apos;&lt;/span&gt;
                },

                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;&quot;Hamid&quot;&lt;/span&gt;,
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;:&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,
