        &lt;span class=&quot;hljs-string&quot;&gt;&quot;data&quot;&lt;/span&gt;: [{
                  &lt;span class=&quot;hljs-string&quot;&gt;&quot;id&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;,
                  &lt;span class=&quot;hljs-string&quot;&gt;&quot;name&quot;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;foo&quot;&lt;/span&gt;
         }]
      };

                &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; obj=&lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;parse&lt;/span&gt;(stringdata);
                &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; key = &lt;span class=&quot;hljs-string&quot;&gt;&quot;message&quot;&lt;/span&gt;;
                &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(obj[key]);
                &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; keyobj = &lt;span class=&quot;hljs-string&quot;&gt;&quot;data&quot;&lt;/span&gt;;
                &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ArrObj&lt;/span&gt; =obj[keyobj];

                &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;ArrObj&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;);
