&lt;p&gt;This is a variation of @mikez302&apos;s answer, but modified to support numbers with decimals (per @neu-rah&apos;s feedback that numberWithCommas(12345.6789) -&amp;gt; &quot;12,345.6,789&quot; instead of &quot;12,345.6789&quot;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;numberWithCommas&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;n&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; parts=n.&lt;span class=&quot;hljs-title function_&quot;&gt;toString&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;.&quot;&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; parts[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;replace&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\B(?=(\d{3})+(?!\d))/g&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;) + (parts[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] ? &lt;span class=&quot;hljs-string&quot;&gt;&quot;.&quot;&lt;/span&gt; + parts[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;] : &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;);
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;formatNumber&lt;/span&gt; (num) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; num.&lt;span class=&quot;hljs-title function_&quot;&gt;toString&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;replace&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/(\d)(?=(\d{3})+(?!\d))/g&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;$1,&quot;&lt;/span&gt;)
}

&lt;span class=&quot;hljs-title function_&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNumber&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;2665&lt;/span&gt;));      &lt;span class=&quot;hljs-comment&quot;&gt;// 2,665&lt;/span&gt;
&lt;span class=&quot;hljs-title function_&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNumber&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;102665&lt;/span&gt;));    &lt;span class=&quot;hljs-comment&quot;&gt;// 102,665&lt;/span&gt;
&lt;span class=&quot;hljs-title function_&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNumber&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;111102665&lt;/span&gt;)); &lt;span class=&quot;hljs-comment&quot;&gt;// 111,102,665&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Using Regular expression&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;toCommas&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;value&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; value.&lt;span class=&quot;hljs-title function_&quot;&gt;toString&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;replace&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\B(?=(\d{3})+(?!\d))/g&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;);
}
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;toCommas&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;123456789&lt;/span&gt;)); &lt;span class=&quot;hljs-comment&quot;&gt;// 123,456,789&lt;/span&gt;

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;toCommas&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1234567890&lt;/span&gt;)); &lt;span class=&quot;hljs-comment&quot;&gt;// 1,234,567,890&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;toCommas&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;1234&lt;/span&gt;)); &lt;span class=&quot;hljs-comment&quot;&gt;// 1,234&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Using toLocaleString()&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; number = &lt;span class=&quot;hljs-number&quot;&gt;123456.789&lt;/span&gt;;

&lt;span class=&quot;hljs-comment&quot;&gt;// request a currency format&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(number.&lt;span class=&quot;hljs-title function_&quot;&gt;toLocaleString&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;de-DE&apos;&lt;/span&gt;, { &lt;span class=&quot;hljs-attr&quot;&gt;style&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;currency&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;currency&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;EUR&apos;&lt;/span&gt; }));
&lt;span class=&quot;hljs-comment&quot;&gt;//  123.456,79 &lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;// the Japanese yen doesn&apos;t use a minor unit&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(number.&lt;span class=&quot;hljs-title function_&quot;&gt;toLocaleString&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;ja-JP&apos;&lt;/span&gt;, { &lt;span class=&quot;hljs-attr&quot;&gt;style&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;currency&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;currency&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;JPY&apos;&lt;/span&gt; }))
&lt;span class=&quot;hljs-comment&quot;&gt;//  123,457&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;// limit to three significant digits&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(number.&lt;span class=&quot;hljs-title function_&quot;&gt;toLocaleString&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;en-IN&apos;&lt;/span&gt;, { &lt;span class=&quot;hljs-attr&quot;&gt;maximumSignificantDigits&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; }));
&lt;span class=&quot;hljs-comment&quot;&gt;//  1,23,000&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;ref &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Number/toLocaleString&quot; rel=&quot;noreferrer&quot;&gt;MDN:Number.prototype.toLocaleString()&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Using Intl.NumberFormat()&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; number = &lt;span class=&quot;hljs-number&quot;&gt;123456.789&lt;/span&gt;;

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Intl&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;NumberFormat&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;de-DE&apos;&lt;/span&gt;, { &lt;span class=&quot;hljs-attr&quot;&gt;style&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;currency&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;currency&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;EUR&apos;&lt;/span&gt; }).&lt;span class=&quot;hljs-title function_&quot;&gt;format&lt;/span&gt;(number));
&lt;span class=&quot;hljs-comment&quot;&gt;// expected output: &quot;123.456,79 &quot;&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;// the Japanese yen doesn&apos;t use a minor unit&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Intl&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;NumberFormat&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;ja-JP&apos;&lt;/span&gt;, { &lt;span class=&quot;hljs-attr&quot;&gt;style&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;currency&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;currency&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;JPY&apos;&lt;/span&gt; }).&lt;span class=&quot;hljs-title function_&quot;&gt;format&lt;/span&gt;(number));
&lt;span class=&quot;hljs-comment&quot;&gt;// expected output: &quot;123,457&quot;&lt;/span&gt;

&lt;span class=&quot;hljs-comment&quot;&gt;// limit to three significant digits&lt;/span&gt;
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Intl&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;NumberFormat&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;en-IN&apos;&lt;/span&gt;, { &lt;span class=&quot;hljs-attr&quot;&gt;maximumSignificantDigits&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; }).&lt;span class=&quot;hljs-title function_&quot;&gt;format&lt;/span&gt;(number));

&lt;span class=&quot;hljs-comment&quot;&gt;// expected output: &quot;1,23,000&quot;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;ref &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/NumberFormat&quot; rel=&quot;noreferrer&quot;&gt;Intl.NumberFormat&lt;/a&gt;&lt;/p&gt;

