    var1 =&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;;
  };
  &lt;span class=&quot;hljs-built_in&quot;&gt;Obj&lt;/span&gt;(&lt;span class=&quot;hljs-type&quot;&gt;const&lt;/span&gt; Obj &amp;amp; org){
