            intent.&lt;span class=&quot;hljs-title function_&quot;&gt;putExtra&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Mode&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;Gallery&quot;&lt;/span&gt;);
